import React, { useState } from 'react';
import Fuse from 'fuse.js';
import text from './dictionary.txt'

// import characters from './characters.json';


let instance  = [];
export  default function Search() {
  const [query, updateQuery] = useState('');

  const list = ['Kelly', 'Creed', 'Stanley','Crowd','Steel'];
   fetch(text).then(function(response) {
    response.text().then(function(text) {
    instance =text;
    console.log(instance);
    });

  });

  fetch(text)
   .then( r => r.text() )
   .then( t => instance=t )


// const fs = require("fs");
// const customerFile = fs.readFileSync(text, "utf-8");
// const customerArr = customerFile.split("n");
// customerArr.map(i => JSON.parse(i));
console.log("hello")
console.log(instance);

  // const demo = list.length();
  let options = {
  shouldSort: true,
  tokenize: true,
  matchAllTokens: true,
  findAllMatches: true,
  threshold: 0,
  location: 0,
  distance: 0,
  maxPatternLength: 32,
  minMatchCharLength: 1,

    // don't include the keys property
  };

  const fuse = new Fuse(list, options);
  // console.log(list)
  const results = fuse.search(query);
  const characterResults = results.length !== 0 ? results.map(character => character.item) : list;

  function onSearch({ currentTarget }) {
    updateQuery(currentTarget.value);
  }

  return (
    <>

      <header>
        <div >
          <h1>Characters</h1>
        </div>
      </header>

      <main>
        <ul>
          {characterResults.map(character => {
            return (
              <li key={character}>
                <ul>
                  <li>
                    <strong>Name:</strong> { character }
                  </li>
                </ul>
              </li>
            )
          })}
        </ul>
        <aside>
          <form className="search">
            <label>Search</label>
            <input type="text" value={query} onChange={onSearch} />
          </form>
        </aside>
      </main>

    </>
  );
}
