  shouldSort: true,
  tokenize: true,
  matchAllTokens: true,
  findAllMatches: true,
  threshold: 0,
  location: 0,
  distance: 0,
  maxPatternLength: 32,
  minMatchCharLength: 1,

    // don't include the keys property
  };

  const onChange = (e) => {
    updateQuery(e.target.value);
  };


