import React, { useState } from 'react';
import Fuse from 'fuse.js';
import text from './dictionary.txt';

const Search = () => {
  const [query, updateQuery] = useState('');
  const [instance, setInstance]=useState();
  const [characterResults,setCharacterResults]=useState([]);
  // let list = ['Please','Enter','Keyword'];



  fetch(text)
    .then((response) => response.text())
    .then((result) => {
      setInstance(result.split("\n"));
    });


  let options = {
  shouldSort: true,
  tokenize: true,
  matchAllTokens: true,
  findAllMatches: true,
  threshold: 0,
  location: 0,
  distance: 0,
  maxPatternLength: 32,
  minMatchCharLength: 1,

    // don't include the keys property
  };

  const onChange = (e) => {
    updateQuery(e.target.value);
  };


  const onSearch = () => {
    const fuse = new Fuse(instance , options);
    // console.log(list)
    const results = fuse.search(query);

    if(results.length!== 0)
    {
      setCharacterResults (results.map(character => character.item));
    }
    else
    {
      setCharacterResults("00000");
    } 

  };

  const handleAddValue = () => {
    // const dist=/
    setInstance([...instance,query]);
    console.log(instance);
    // console.log(query)
  };  

  return (
    <>

      <header>
        <div >
          <h1>Characters</h1>
        </div>
      </header>

      <main>
        <h3>{characterResults !== "00000" ? (characterResults.map(ch => 
          <li>
          {ch}
          </li>
          )
        ):
        <li>
          <p>Add this value into the Dictionary</p>
         <button type="button" onClick={() => handleAddValue()}>Add</button>
        </li>
        }
          </h3>
        <aside>
          <form>
            <label>Search</label>
            <input type="text" value={ query} onChange={onChange}/>
            <button type="button" onClick={onSearch}>Search</button>
          </form>
        </aside>
      </main>

    </>
  );

}

export default Search;
