import React, { useState } from 'react';
import Fuse from 'fuse.js';
import text from './dictionary.txt'

// import characters from './characters.json';


export  default function Search() {
  const [query, updateQuery] = useState('');
  const [instance,setInstance]=useState([]);

  let list = ['Kellu','whlsds','sasa'];
  //  fetch(text).then(function(response) {
  //   response.text().then(function(text) {
  //     setInstance(text.split("\n"));
  //   // console.log(instance); 
  //   });

  // });


  let options = {
  shouldSort: true,
  tokenize: true,
  matchAllTokens: true,
  findAllMatches: true,
  threshold: 0,
  location: 0,
  distance: 0,
  maxPatternLength: 32,
  minMatchCharLength: 1,

    // don't include the keys property
  };
function change ({currentTarget}){
  updateQuery(currentTarget.value );
}

let characterResults=null;
  function onSearch({ currentTarget }) {

    const fuse = new Fuse(list , options);
    // console.log(list)
    const results = fuse.search(query);

     characterResults = results.length !== 0 ? results.map(character => character.item) : list;
  }

  return (
    <>

      <header>
        <div >
          <h1>Characters</h1>
        </div>
      </header>

      <main>
        <ul>
          {characterResults.map(character => {
            return (
              <li key={character}>
                <ul>
                  <li>
                    <strong>Name:</strong> { character }
                  </li>
                </ul>
              </li>
            )
          })}
        </ul>
        <aside>
          <form>
            <label>Search</label>
            <input type="text" value={ query} onChange={change}/>
            <button type="button"   onClick={onSearch(query)}>Hello</button>
          </form>
        </aside>
      </main>

    </>
  );

}
